mapper.select:
SELECT DISTINCTROW p.* FROM system_user u JOIN system_user_role ur ON ur.user_id = u.id JOIN system_role r ON ur.role_id = r.id JOIN system_role_permission rp ON rp.role_id = r.id JOIN system_permission p ON rp.permission_id = p.id
p.whether_route = 1 AND u.whether_delete = 0 AND r.whether_delete = 0 AND p.whether_delete = 0
AND u.id = #{userId}

SELECT DISTINCTROW p.* FROM system_permission p JOIN system_role_permission rp ON rp.permission_id = p.id JOIN system_role r ON rp.role_id = r.id
p.whether_route = 1 AND r.whether_delete = 0 AND p.whether_delete = 0
AND r.id = #{roleId}

SELECT DISTINCTROW p.code FROM system_user u JOIN system_user_role ur ON ur.user_id = u.id JOIN system_role r ON ur.role_id = r.id JOIN system_role_permission rp ON rp.role_id = r.id JOIN system_permission p ON rp.permission_id = p.id
p.whether_route = 1 AND u.whether_delete = 0 AND r.whether_delete = 0 AND p.whether_delete = 0
AND u.id = #{userId}
